import React, { useState } from 'react';

function SolutionsPanel({ solutions, onSelectSolution }) {
  const [selectedSolution, setSelectedSolution] = useState(null);

  if (!solutions || solutions.length === 0) {
    return (
      <div className="panel solutions-panel">
        <h3>ðŸ’¡ Recommended Solutions</h3>
        <p className="no-data">Solutions will appear here as the call progresses...</p>
      </div>
    );
  }

  const handleSelectSolution = (solution, idx) => {
    setSelectedSolution(idx);
    if (onSelectSolution) {
      onSelectSolution(solution);
    }
  };

  return (
    <div className="panel solutions-panel">
      <h3>ðŸ’¡ Recommended Solutions</h3>
      <p className="instruction-text">Click on a solution to help the agent apply it</p>
      <div className="solutions-list">
        {solutions.map((solution, idx) => (
          <div
            key={idx}
            className={`solution-card ${selectedSolution === idx ? 'selected' : ''}`}
            onClick={() => handleSelectSolution(solution, idx)}
            style={{ cursor: 'pointer' }}
          >
            <div className="solution-header">
              <span className="solution-rank">#{solution.rank || idx + 1}</span>
              <span className="solution-title">{solution.title}</span>
              <span className={`confidence-badge ${solution.confidence > 85 ? 'high' : solution.confidence > 70 ? 'medium' : 'low'}`}>
                {solution.confidence}% confidence
              </span>
            </div>

            {solution.reasoning && (
              <div className="solution-reasoning">
                <strong>Why:</strong> {solution.reasoning}
              </div>
            )}

            {solution.steps && solution.steps.length > 0 && (
              <div className="solution-steps">
                <strong>Steps:</strong>
                <ol>
                  {solution.steps.map((step, stepIdx) => (
                    <li key={stepIdx}>{step}</li>
                  ))}
                </ol>
              </div>
            )}

            {selectedSolution === idx && (
              <div className="selected-indicator">âœ“ Agent is applying this solution</div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default SolutionsPanel;
