import React, { useEffect, useRef, useState } from 'react';

function TranscriptPanel({ transcript, active, callStartTime }) {
  const transcriptEndRef = useRef(null);
  const [elapsedTime, setElapsedTime] = useState(0);

  useEffect(() => {
    transcriptEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [transcript]);

  useEffect(() => {
    let interval;
    if (active && callStartTime) {
      interval = setInterval(() => {
        const elapsed = Math.floor((Date.now() - callStartTime) / 1000);
        setElapsedTime(elapsed);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [active, callStartTime]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="panel transcript-panel">
      <div className="transcript-header">
        <h3>
          üìù Live Transcript
          {active && <span className="live-indicator">‚óè LIVE</span>}
        </h3>
        {callStartTime && (
          <div className="call-timer">
            <span className="timer-icon">‚è±Ô∏è</span>
            <span className="timer-value">{formatTime(elapsedTime)}</span>
          </div>
        )}
      </div>

      <div className="transcript-container">
        {transcript.length === 0 ? (
          <p className="no-data">Waiting for call to start...</p>
        ) : (
          <>
            {transcript.map((line, idx) => (
              <div key={idx} className={`transcript-line ${line.speaker.toLowerCase()}`}>
                <span className="timestamp">[{line.timestamp}]</span>
                <span className="speaker">{line.speaker}:</span>
                <span className="text">{line.text}</span>
              </div>
            ))}
            <div ref={transcriptEndRef} />
          </>
        )}
      </div>
    </div>
  );
}

export default TranscriptPanel;
