import React, { useState, useEffect } from 'react';
import './App.css';
import { api, createWebSocket } from './services/api';
import CustomerPanel from './components/CustomerPanel';
import TranscriptPanel from './components/TranscriptPanel';
import AIInsights from './components/AIInsights';
import SolutionsPanel from './components/SolutionsPanel';
import MetricsBar from './components/MetricsBar';

// Fake solutions for Call 1 (Billing)
const CALL1_SOLUTIONS = [
  {
    resolution_id: "RES-BILL-001",
    title: "Apply 50% Roaming Courtesy Credit",
    category: "BILLING",
    confidence: 95,
    rank: 1,
    reasoning: "Customer incurred $67 in unexpected roaming charges during Canada trip. As a 3+ year loyal customer with perfect payment history, a 50% courtesy credit demonstrates goodwill and retention focus.",
    steps: [
      "Verify roaming charges: $67.44 from May 8-12 Canada trip",
      "Acknowledge customer wasn't aware of international roaming policy",
      "Apply 50% courtesy credit ($33.72) immediately",
      "Explain TravelPass option for future trips ($10/day vs $67 total)",
      "Update customer profile with travel preferences"
    ]
  },
  {
    resolution_id: "RES-BILL-002",
    title: "Set Up TravelPass for Future Trips",
    category: "BILLING",
    confidence: 88,
    rank: 2,
    reasoning: "Customer travels to Canada multiple times yearly for work. TravelPass auto-activation would have cost $50 vs $67, saving money and preventing billing surprises.",
    steps: [
      "Explain TravelPass auto-activation feature",
      "Show savings: 5-day trip = $50 with TravelPass vs $67 current charges",
      "Add TravelPass to customer profile (no upfront cost)",
      "Provide quick reference guide for supported countries",
      "Set reminder to follow up after next trip"
    ]
  },
  {
    resolution_id: "RES-BILL-003",
    title: "Review Account for Additional Loyalty Credits",
    category: "BILLING",
    confidence: 76,
    rank: 3,
    reasoning: "Long-term loyal customer (3+ years) may qualify for retention offers or loyalty discounts to offset billing surprise.",
    steps: [
      "Check customer tenure and payment history",
      "Review recent billing patterns for anomalies",
      "Apply available loyalty discounts or promotions",
      "Offer autopay discount if not enrolled ($5-10/month)",
      "Document goodwill gesture in customer notes"
    ]
  }
];

// Fake solutions for Call 2 (Network Outage)
const CALL2_SOLUTIONS = [
  {
    resolution_id: "RES-TECH-001",
    title: "Enable WiFi Calling + $35 Service Credit",
    category: "TECHNICAL",
    confidence: 96,
    rank: 1,
    reasoning: "Customer experiencing 3+ hour network outage in West Hollywood (90069) affecting work-from-home business. WiFi calling provides immediate workaround while tower repair completes. Premium customer deserves service credit.",
    steps: [
      "Confirm tower outage at West Hollywood site (ETA 4:00 PM)",
      "Verify customer has WiFi access at home",
      "Guide WiFi calling setup: Settings ‚Üí Connections ‚Üí WiFi Calling",
      "Apply $35 service disruption credit to account",
      "Set up SMS/email notification when service restored",
      "Document business impact for network ops team"
    ]
  },
  {
    resolution_id: "RES-TECH-002",
    title: "Escalate to Priority Restoration Team",
    category: "TECHNICAL",
    confidence: 89,
    rank: 2,
    reasoning: "3+ hour outage severely impacting customer's work-from-home business operations. Premium plan customer requires priority support and regular updates.",
    steps: [
      "Create priority ticket documenting business impact",
      "Escalate to Network Operations Center supervisor",
      "Request expedited restoration status",
      "Provide direct callback number for updates",
      "Schedule hourly follow-up until service restored",
      "Offer temporary mobile hotspot if available"
    ]
  },
  {
    resolution_id: "RES-TECH-003",
    title: "Set Up Proactive Outage Notifications",
    category: "TECHNICAL",
    confidence: 81,
    rank: 3,
    reasoning: "Customer missed 3 hours of critical work time due to lack of outage notification. Setting up proactive alerts prevents future frustration and enables immediate workaround activation.",
    steps: [
      "Apologize for notification system failure",
      "Verify SMS (555-876-4321) and email alerts enabled",
      "Enable network status push notifications",
      "Provide status page: status.telcomax.com/90069",
      "Test alert system with confirmation message",
      "Add $10 credit for notification delay"
    ]
  }
];

function App() {
  const [callActive, setCallActive] = useState(false);
  const [customer, setCustomer] = useState(null);
  const [billing, setBilling] = useState(null);
  const [transcript, setTranscript] = useState([]);
  const [fullTranscript, setFullTranscript] = useState('');
  const [aiInsights, setAIInsights] = useState(null);
  const [solutions, setSolutions] = useState([]);
  const [ws, setWs] = useState(null);
  const [callId, setCallId] = useState(null);
  const [callStartTime, setCallStartTime] = useState(null);
  const [callSummary, setCallSummary] = useState(null);
  const [selectedSolution, setSelectedSolution] = useState(null);

  const startDemoCall = async (demoCallId, customerId) => {
    setCallActive(true);
    setTranscript([]);
    setFullTranscript('');
    setAIInsights(null);
    setSolutions([]);
    setCallId(demoCallId);
    setCallStartTime(Date.now());
    setCallSummary(null);
    setSelectedSolution(null);

    // Load customer data
    try {
      const customerData = await api.getCustomer(customerId);
      setCustomer(customerData.data);

      const billingData = await api.getBilling(customerId);
      setBilling(billingData.data);
    } catch (error) {
      console.error('Error loading customer data:', error);
    }

    // Progressive solution generation - choose solutions based on call
    const solutionSet = demoCallId === 'CALL-001' ? CALL1_SOLUTIONS : CALL2_SOLUTIONS;

    // Add solutions progressively during the call
    // Solution 1 appears after ~8 seconds
    setTimeout(() => {
      setSolutions([solutionSet[0]]);
    }, 8000);

    // Solution 2 appears after ~18 seconds
    setTimeout(() => {
      setSolutions([solutionSet[0], solutionSet[1]]);
    }, 18000);

    // Solution 3 appears after ~30 seconds
    setTimeout(() => {
      setSolutions([solutionSet[0], solutionSet[1], solutionSet[2]]);
    }, 30000);

    // Connect WebSocket
    const websocket = createWebSocket(demoCallId);
    setWs(websocket);

    websocket.onopen = () => {
      console.log('WebSocket connected');
    };

    websocket.onmessage = async (event) => {
      const message = JSON.parse(event.data);

      if (message.type === 'transcript_line') {
        const newTranscript = [...transcript, message.data];
        setTranscript(newTranscript);

        // Update full transcript
        const newFullTranscript = fullTranscript + ' ' + message.data.text;
        setFullTranscript(newFullTranscript);

        // Fetch AI insights and solutions early and keep updating
        // First update: After 2 lines (5-10 seconds)
        // Then update every 3 lines to keep solutions fresh
        const shouldUpdate = newTranscript.length === 2 || (newTranscript.length > 2 && newTranscript.length % 3 === 0);

        if (shouldUpdate) {
          try {
            const insights = await api.summarizeCall(newFullTranscript, customerId);
            setAIInsights(insights.data);

            // Get solutions based on early context
            const solutionsData = await api.getSuggestions(
              newFullTranscript,
              customerId,
              insights.data.issue_category
            );
            setSolutions(solutionsData.data.solutions || []);
          } catch (error) {
            console.error('Error updating AI insights:', error);
          }
        }
      } else if (message.type === 'call_end') {
        setCallActive(false);

        // Generate call summary
        const summary = {
          issue_category: aiInsights?.issue_category || 'General Inquiry',
          resolution: selectedSolution ? selectedSolution.title : 'Resolved via agent assistance',
          final_sentiment: aiInsights?.sentiment || 'SATISFIED',
          next_steps: selectedSolution ? selectedSolution.steps?.join(', ') : 'Follow-up scheduled if needed'
        };
        setCallSummary(summary);

        websocket.close();
      }
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    websocket.onclose = () => {
      console.log('WebSocket closed');
      setCallActive(false);
    };
  };

  const handleSelectSolution = (solution) => {
    setSelectedSolution(solution);
    console.log('Agent selected solution:', solution.title);
  };

  const goHome = () => {
    // Close WebSocket if active
    if (ws) {
      ws.close();
    }

    // Reset all state
    setCallActive(false);
    setCustomer(null);
    setBilling(null);
    setTranscript([]);
    setFullTranscript('');
    setAIInsights(null);
    setSolutions([]);
    setWs(null);
    setCallId(null);
    setCallStartTime(null);
    setCallSummary(null);
    setSelectedSolution(null);
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>ü§ñ ProcAI Agent Dashboard</h1>
        <div className="header-controls">
          {!callActive ? (
            <div className="demo-buttons">
              <button onClick={() => startDemoCall('CALL-001', 'TC-887234')} className="btn-primary">
                üìû Start Demo Call 1 (Billing)
              </button>
              <button onClick={() => startDemoCall('CALL-002', 'TC-923461')} className="btn-primary">
                üìû Start Demo Call 2 (Outage)
              </button>
            </div>
          ) : (
            <div className="call-controls">
              <div className="call-indicator">
                <span className="recording-dot"></span> Call in Progress
              </div>
              <button onClick={goHome} className="btn-home">
                üè† Home
              </button>
            </div>
          )}
        </div>
      </header>

      <MetricsBar />

      <div className="dashboard-grid">
        <div className="left-panel">
          <CustomerPanel customer={customer} billing={billing} />
        </div>

        <div className="center-panel">
          <TranscriptPanel
            transcript={transcript}
            active={callActive}
            callStartTime={callStartTime}
          />
          <AIInsights insights={aiInsights} customer={customer} />
        </div>

        <div className="right-panel">
          <SolutionsPanel
            solutions={solutions}
            onSelectSolution={handleSelectSolution}
            callActive={callActive}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
